
public class Q214_Shortest_Palindrome {
	public String shortestPalindrome(String s) {
        if (s == null || s.length() == 0) {
            return s;
        }
        
        int len = s.length();
        
        for(int i = len - 1; i >= 0; i--) {
            if(isPalindrome(s, 0, i) == true) {
                String suffix = s.substring(i + 1, len);
                return new StringBuilder(suffix).reverse().toString() + s;
            }
        }
        
        return new StringBuilder(s).reverse().toString() + s;
    }
    
    public boolean isPalindrome(String s, int start, int end) {
        while(start < end) {
            if(s.charAt(start) != s.charAt(end)) {
                return false;
            } 
            
            start++;
            end--;
        }
        
        return true;
    }
	
	
	public String shortestPalindrome2(String s) {
        if(s == null || s.length() == 0){
            return "";
        }
        
        int len = s.length();
        int maxLen = 0;
        
        for(int i = 0; i <= len / 2; i++){
            maxLen = Math.max(maxLen, Math.max(longestPalindrome(s, i, i), longestPalindrome(s, i, i + 1)));
        }
        
        String suffix = s.substring(maxLen + 1);        
        return new StringBuilder(suffix).reverse().toString() + s.substring(0, maxLen + 1) + suffix;
    }
    
    public int longestPalindrome(String s, int left, int right){
        while(left >= 0 && right < s.length() && s.charAt(left) == s.charAt(right)){
            left--;
            right++;
        }
        
        if(left == -1){
            return right - 1;
        } else {
            return -1;
        }
    }
	
    // follow up: to find min Palindrome either from left or right side
    public String shortestPalindrome_follow_up(String s){
    	if(s == null || s.length() == 0){
            return "";
        }
    	
    	int len = s.length();
        int leftMaxLen = 0;
        int rightMaxLen = 0;
        
        for(int i = 0; i <= len / 2; i++){
        	leftMaxLen = Math.max(leftMaxLen, Math.max(longestLeftPalindrome(s, i, i), longestLeftPalindrome(s, i, i + 1)));
        }
        
        for(int i = len / 2 + 1; i < len; i++){
        	rightMaxLen = Math.max(rightMaxLen, Math.max(longestRightPalindrome(s, i, i), longestRightPalindrome(s, i, i + 1)));
        }
        
        if(leftMaxLen > rightMaxLen){
        	String suffix = s.substring(leftMaxLen + 1);        
            return new StringBuilder(suffix).reverse().toString() + s;//s.substring(0, leftMaxLen + 1) + suffix;
        } else {
        	String suffix = s.substring(0, len - rightMaxLen);        
            return s + new StringBuilder(suffix).reverse().toString(); 
        }   	
    } 
    
    public int longestLeftPalindrome(String s, int left, int right){
        while(left >= 0 && right < s.length() && s.charAt(left) == s.charAt(right)){
            left--;
            right++;
        }
        
        if(left == -1){
            return right - 1;
        } else {
            return -1;
        }
    }
    
    public int longestRightPalindrome(String s, int left, int right){
        while(left >= 0 && right < s.length() && s.charAt(left) == s.charAt(right)){
            left--;
            right++;
        }
        
        if(right == s.length()){
            return right - left - 1;
        } else {
            return -1;
        }
    }
	
	
	/*************************************************/
	// by other, nice!!!
	public String shortestPalindrome3(String s) {
		int j = 0;
	    for (int i = s.length() - 1; i >= 0; i--) {
	        if (s.charAt(i) == s.charAt(j)) { 
	        	j++; 
	        }
	    }
	    
	    if (j == s.length()) {
	    	return s; 
	    }
	    
	    String suffix = s.substring(j);
	    return new StringBuffer(suffix).reverse().toString() + shortestPalindrome(s.substring(0, j)) + suffix;   // 注意这里继续递归 ！！！
    }
	
	
	
	/*************************************************/
	// by Jackie, but exceed time limit
	public String shortestPalindrome4(String s) {
        if(s == null || s.length() == 0){
            return new String();
        } else if(isPalindrome(s) == true){
            return s;
        }
        
        int n = s.length();
        int leftNum = 0, rightNum = 0;
        for(int end = n; end > 0; end--){
            if(isPalindrome(s.substring(0, end)) == true){
                leftNum = end;
                break;
            }
        }
        
        for(int start = 0; start < n; start++){
            if(isPalindrome(s.substring(start)) == true){
                rightNum = n - start;
                break;
            }
        }
        
        if(leftNum > rightNum){
            String part1 = s.substring(0, leftNum);
            String part2 = s.substring(leftNum);
            StringBuffer builder = new StringBuffer(part2);
            builder.reverse();
            builder.append(part1).append(part2);
            return builder.toString();
        } else {
            String part1 = s.substring(n - rightNum);
            String part2 = s.substring(0, n - rightNum);
            StringBuffer builder = new StringBuffer(s);
            StringBuffer builder2 = new StringBuffer(part2);
            builder2.reverse();
            builder.append(builder2);
            System.out.println(builder.length());
            return builder.toString();
        }
    }
    
    public boolean isPalindrome(String str){
        int left = 0, right = str.length() - 1;
        while(left < right){
            if(str.charAt(left) != str.charAt(right)){
                return false;
            }
            left++;
            right--;
        }
        return true;
    }
    
    
    
    public static void main(String[] args){
    	Q214_Shortest_Palindrome t = new Q214_Shortest_Palindrome();
    	String str1 = "adcba";
    	String str2 = "efabccba";
    	String str3 = "";
    	String str4 = "aacecaaa";
    	String str5 = "treabcba";
    	String str6 = "abcbatre";
    	System.out.println(t.shortestPalindrome(str6));
    	System.out.println(t.shortestPalindrome_follow_up(str6));
    }
}
